import { CST } from "../CST";

export class MenuScene extends Phaser.Scene {
    constructor() {
        super({
            key: CST.SCENCES.MENU
        });
    }

    init(data) {
        console.log(data);
        console.log("I got it!");
    }

    preload() {

    }

    create() {
        
        // Create Images
        this.add.image(this.game.renderer.width / 2, this.game.renderer.height * 0.20, "logo").setDepth(1);
        this.add.image(0,0, "title_bg").setOrigin(0, 0).setDepth(0);
        let playButton = this.add.image(this.game.renderer.width / 2, this.game.renderer.height / 2, "play_button").setDepth(1);
        let optionsButton = this.add.image(this.game.renderer.width / 2, this.game.renderer.height / 2 + 100, "options_button").setDepth(1);

        // Create Sprites
        let hoverSprite = this.add.sprite(100,100, "cat");
        hoverSprite.setScale(2);
        hoverSprite.setVisible(false);

        // Create Audio
        // this.sound.pauseOnBlur = false;
        this.sound.play("title_music", {
            loop: true
        });

        // Create Animations
        this.anims.create({
            key: "walk",
            frameRate: 4,
            repeat: -1,  // Repeat Forever
            frames: this.anims.generateFrameNames("cat", {
                frames: [0, 1, 2, 3]
            })
        })

        /*
            PointerEvents:
                pointerover - Hovering
                pointerout - Not Hovering
                pointerup - Click and Release
                pointerdown - Just Click
        */

        // Config Play Button
        playButton.setInteractive();
        playButton.on("pointerover", () => {
            hoverSprite.setVisible(true);
            hoverSprite.x = playButton.x - playButton.width;
            hoverSprite.y = playButton.y;
            hoverSprite.anims.play("walk");
        });
        playButton.on("pointerout", () => {
            hoverSprite.setVisible(false);
            hoverSprite.anims.pause();
        });
        playButton.on("pointerup", () => {
            console.log("open the gates");
        });

        // Config Options Button
        optionsButton.setInteractive();
        optionsButton.on("pointerover", () => {
            hoverSprite.setVisible(true);
            hoverSprite.x = optionsButton.x - optionsButton.width;
            hoverSprite.y = optionsButton.y;
            hoverSprite.anims.play("walk");
        });
        optionsButton.on("pointerout", () => {
            hoverSprite.setVisible(false);
            hoverSprite.anims.pause();
        });
        optionsButton.on("pointerup", () => {
            console.log("open the gates");
        });
    }

    update() {
    }
}
